import React, { useState, useContext } from "react";
import axios from "axios";
import { ExpenseContext } from "./ExpenseProvider";

const BudgetForm = () => {
  const [budgetValue, setBudgetValue] = useState("");
  const { budget, setBudget } = useContext(ExpenseContext);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (budgetValue !== "" && !isNaN(Number(budgetValue))) {
      const newBudget = parseFloat(budgetValue);
      setBudget(newBudget);
      setBudgetValue("");

      // Update your endpoint URL and data key accordingly
      try {
        const response = await axios.patch('http://localhost:3333/budget', { budget: newBudget });
        console.log(response.data);
      } catch (error) {
        console.error('An error occurred while updating the budget:', error);
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="number"
        placeholder="Enter Budget"
        value={budgetValue}
        onChange={(e) => setBudgetValue(e.target.value)}
      />
      <button type="submit">Set Budget</button>
    </form>
  );
}

export default BudgetForm;
