import React, { useState } from "react";
interface Expense {
  description: string;
  amount: number;
  classification: string;
  hasReceipt: string;
}


interface Props {
  addExpense: (expense: Expense) => void;
}

const ExpenseForm: React.FC<Props> = ({ addExpense }) => {
  const [description, setDescription] = useState("");
  const [amount, setAmount] = useState("");
  const [classification, setClassification] = useState("");
  const [hasReceipt, setHasReceipt] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (description.trim() !== "" && amount !== "" && classification !== "" && hasReceipt !== "") {
      const expense = {
        description,
        amount: parseFloat(amount),
        classification,
        hasReceipt
      };
      addExpense(expense);
      setDescription("");
      setAmount("");
      setClassification("");
      setHasReceipt("");
    }
  };


  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <input
        type="number"
        placeholder="Amount"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <input
        type="text"
        placeholder="Classification"
        value={classification}
        onChange={(e) => setClassification(e.target.value)}
      />
      <select
        value={hasReceipt}
        onChange={(e) => setHasReceipt(e.target.value)}
      >
        <option value="">Has Receipt?</option>
        <option value="Yes">Yes</option>
        <option value="No">No</option>
      </select>
      <button type="submit">Add Expense</button>
    </form>
  );
}



export default ExpenseForm;
